import React, { Component } from 'react';

import {
    postRemoveUserFromCompletionOfStepAPICALL,
    postAddUserToCompletionOfStepAPICALL
} from '../../utils/requests';
import {withRouter} from 'react-router-dom';
import ReactCardFlip from 'react-card-flip';
import SearchBar from 'react-search-bar';
import "react-datepicker/dist/react-datepicker.css";
import { CSVLink } from "react-csv";

let csvData= [["First Name", "Last Name", "Email", "Phone Number"]];
let currentUsers;

class ViewCompletions extends Component {
  constructor (props) {
    super(props);
    this.state = {
        users: [],
        isFlipped: false,
        toSearch: "",
    };

    console.log("___________________________________________________________________________");
    console.log("CompletionIDs");
    console.log(this.props.history.location.state.CompletionsReady);
      // schemaParticipants.properties.listOfStrings.push(this.props.history.location.state.ParticipantIDs[0]);

      this.setState({
          users: this.props.history.location.state.CompletionsReady
      });

    console.log(this.props.history.location.state)
    // Bind
      this.handleClickCard = this.handleClickCard.bind(this);
      this.handleSearch = this.handleSearch.bind(this);
  }


    handleClickCard(e) {
      //Move to update user
        e.preventDefault();
        this.setState(prevState => ({ isFlipped: !prevState.isFlipped }));
        console.log("Switching to:" + this.state.isFlipped);
    }

    handleSearch(text, currentUsers){

        console.log(text);

        //Check if it contains digits only and is 5 characters long
        if(!(/^\d+$/.test(text)) || (text.length !== 5)){
            alert("Invalid ID");
            return;
        }

        return postAddUserToCompletionOfStepAPICALL(this.props.history.location.state.hashedIDEvent, this.props.history.location.state.hashedIDStep, text, currentUsers)
            .then((res) => {
                if(res.data.status === "success"){
                    // console.log(res.data.data.userData);
                    alert("User was added successfully");
                    // for (let i = 0; i < this.state.users; i++) {
                    //     this.state.user.unshift(this.state.users[i]);
                    // }
                    // this.setState()
                    // console.log("__________");
                    // console.log(currentUsers);
                    // this.props.history.location.state.CompletionsReady.unshift(res.data.data.userData);
                    // console.log(this.props.history.location.state.CompletionsReady);
                    this.setState({
                        users: this.props.history.location.state.CompletionsReady
                    });
                    alert("User added successfully");

                    // this.forceUpdate();
                }else{
                    alert("An error has occurred while while adding " + text)
                }
            })
    }


    removeUserFromCompletionOfAStep(hashedIDEvent, hashedIDStep, hashedID, auiID){

        return postRemoveUserFromCompletionOfStepAPICALL(hashedIDEvent, hashedIDStep, hashedID)
            .then(isDeleted => {
                return isDeleted;
            })
    }

    // removeUserFromList(hashedID){
    //     console.log("HERE");
    //     console.log(this.state.users.length);
    //     for (let i =0; i < this.state.users.length; i++)
    //         if (this.state.users[i].hashedID === hashedID) {
    //             this.state.users.splice(i,1);
    //             alert("User removed successfully");
    //             this.forceUpdate();
    //             break;
    //         }
    // }

    createTable = () => {
        let table = [];
        this.state.users = this.props.history.location.state.CompletionsReady;

        console.log(this.state.users);
        // Outer loop to create parent

        let arrays = [], size = 5;

        while (this.state.users.length > 0)
            arrays.push(this.state.users.splice(0, size));

        console.log(arrays);

        {arrays.map((array => {
                let children = [];
                //Inner loop to create children
                // for (let j = 0; j < 5; j++) {
                {array.map(participant => {
                    if(participant.isDisplayable){
                        csvData.push([participant.firstName, participant.lastName, participant.email, participant.phoneNumber]);

                        children.push(
                            <td key={participant.hashedID.toString()}>
                                <ReactCardFlip isFlipped={this.state.isFlipped}>
                                    <div key="front" style={styles.card}>
                                        <div style={{float: 'right'}}>
                                            <text style={{fontWeight: "bold", color:'gray'}}>Aui ID: </text>
                                            <text style={{color:'gray'}}>{participant.auiID}<br/></text>
                                        </div>
                                        {
                                            (participant.isNew)?
                                                <text style={{color: "red"}}>New</text>
                                                :<text></text>
                                        }
                                        <text style={{fontWeight: "bold"}}><br/><br/>First Name: </text>
                                        <text>{participant.firstName}<br/></text>
                                        <text style={{fontWeight: "bold"}}>Last Name: </text>
                                        <text>{participant.lastName}<br/></text>
                                        <text style={{fontWeight: "bold"}}>Email: </text>
                                        <text>{participant.email}<br/></text>
                                        <text style={{fontWeight: "bold"}}>Phone Number: </text>
                                        <text>{participant.phoneNumber}<br/></text>
                                        <text style={{fontWeight: "bold"}}>System ID: </text>
